import React from 'react';
import EmergencyDesktopSidebar from './EmergencyDesktopSidebar';
import EmergencyBottomNav from './EmergencyBottomNav';

const EmergencyLayout = ({ children }) => {
  return (
    <div className="min-h-screen bg-gray-50">
      {/* Desktop Sidebar - показывается только на lg+ */}
      <EmergencyDesktopSidebar />

      {/* Main Content - отступ слева на desktop, отступ снизу на mobile */}
      <div className="lg:pl-72 pb-20 lg:pb-0">
        <main className="min-h-screen">
          {children}
        </main>
      </div>

      {/* Bottom Navigation - показывается только на мобильных */}
      <EmergencyBottomNav />
    </div>
  );
};

export default EmergencyLayout;
